def clicked(alphabet):
    global chances
    global sequence
    global pi
    #answer=""
    answer=""
    #answer="12345678901234567890"
    if alphabet == answer[sequence]: #Its checks whether the alphabet is there in the answer
        sequence = sequence +1
        pi=pi+str(alphabet)
        txt=pi
        label2.configure(text=txt)
    else:
        txt="Chances remaining "+str(chances);
        label1.configure(text=txt)
        image = Image.open(image_paths[chances])
        image = image.resize((200, 200), Image.ANTIALIAS)
        imgnew = ImageTk.PhotoImage(image)
        panel.configure(image=imgnew)
        panel.image = imgnew
        chances = chances - 1
        if chances<0:
            messagebox.showinfo("Hanged!!!!!","Points: %d " % (sequence))
            python = sys.executable
            os.execl(python, python, * sys.argv)

def down(e):
    global key
    if key == 0:
        clicked(e.char)
        key = 1

def up(e):
    global key
    if key == 1:
        key = 0

